// import logo from "./logo.svg";
import "./App.css";

// JSX : (Javascript XML): định dạng cho phép viết HTML trong React
// ES6
// Babel: giúp compile code hỗ trợ những trình duyệt cũ
// curly braces: dấu {} ngoặc nhọn

/**
 *
 * element = <div className="info"> Hello world!</div> <-> JSX(nên dùng)
 * element = React.createElement('div', {class: 'info'}) <-> React element
 */

function Feature() {
  return (
    <div className="feature">
      <img src="" alt="" className="feature-image" />
      <h3 className="feature-title">Triggle</h3>
      <p className="feature-desc">
        Lorem ipsum, dolor sit amet consectetur adipisicing elit. Voluptatum
        distinctio eaque in quibusdam? Accusamus, sapiente labore maiores sed
        odit illo maxime facilis sequi libero. Voluptatem mollitia inventore
        vero labore enim!
      </p>
    </div>
  );
}

// Props (truyền vào component YoutubeItem)
function YoutubeItem(props) {
  console.log(props);
  return (
    <div className="">
      <div className="youtube-item">
        <div className="youtube-image">
          <img src={props.image} alt="" />
        </div>
        <div className="youtube-footer">
          <img
            src="https://plus.unsplash.com/premium_photo-1723744998399-3f9882ae00a2?w=400&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxmZWF0dXJlZC1waG90b3MtZmVlZHwyfHx8ZW58MHx8fHx8"
            alt="youtube-avt"
          />
          <div className="youtube-info">
            <h3 className="youtube-title">Learning react</h3>
            <h4 className="youtube-author">Evondev</h4>
          </div>
        </div>
      </div>
    </div>
  );
}
//parent component
function App() {
  return (
    <div className="">
      <h1>Less9</h1>

      <YoutubeItem imgae="https://plus.unsplash.com/premium_photo-1692833836807-7c2ec90aec19?w=400&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxmZWF0dXJlZC1waG90b3MtZmVlZHwxMnx8fGVufDB8fHx8fA%3D%3D"></YoutubeItem>
    </div>
  );
}

export default App;
